import WordpressConnector from './connector';
import logger from '@hoist/logger';
import Moment from 'moment'
import errors from '@hoist/errors';
import config from 'config';

var ConnectorRequiresAuthorizationError = errors.create({
  name: 'ConnectorRequiresAuthorizationError'
});

class WordpressPoller {
  constructor(context) {
    this._logger = logger.child({
      cls: this.constructor.name,
      subscription: context.subscription._id,
      application: context.application._id
    });
    this._context = context;
    this._connector = new WordpressConnector(context.settings);
  }
  poll() {
    return this.assertCanPoll()
      .then((hooksSetup) => {
        if (!hooksSetup) {
          return this.setupHook();
        }
      }).catch((err) => {
        this._logger.error(err);
        if (!(err instanceof ConnectorRequiresAuthorizationError)) {
          this._logger.alert(err);
        }
      });
  }
  assertCanPoll() {
    var hooksSetup = this._context.subscription.get('setup');
    return Promise.resolve()
      .then(() => {
        if (hooksSetup) {
          //we've already setup this subscription
          this._context.subscription.delayTill(new Moment().add(100, 'days').toDate());
        }
      })
      .then(() => {
        this._logger.info('checking credentials');
        if (!(this._context.authorization)) {
          this._logger.warn('Connector needs auth and no auth set');
          //we've already setup this subscription
          this._context.subscription.delayTill(new Moment().add(1, 'hour').toDate());
          throw new ConnectorRequiresAuthorizationError();
        }
        if (!this._context.authorization.get('SubscriptionProject')) {
          this._logger.warn('Connector needs a subscription project and none set');
          //we've already setup this subscription
          this._context.subscription.delayTill(new Moment().add(1, 'hour').toDate());
          throw new ConnectorRequiresAuthorizationError();
        }
      }).then(() => {
        return hooksSetup;
      });
  }
  setupHook() {
    return Promise.resolve()
      .then(() => {
        this._logger.info('setting connector authorization');
        return this._connector.authorize(this._context.authorization);
      }).then(() => {
        this._logger.info('creating webhook endpoint');
        let hookUri = `https://${config.get('Hoist.domains.endpoint')}/connector/${this._context.authorization._token.key}`;
        this._connector.post(`/projects/${this._context.authorization.get('SubscriptionProject')}/hooks`, {
          "url":hookUri,
          "push_events": true,
          "issues_events": true,
          "merge_requests_events": true,
          "tag_push_events": true,
          "note_events": true,
          "build_events": true
        });
      }).then(() => {
        this._logger.info('webhooks created');
        return this._context.subscription.set('setup', true);
      });
  }
}

export default function (context) {
  let poller = new WordpressPoller(context);
  return poller.poll();
};

import WordpressConnector from '../../lib/connector';
import sinon from 'sinon';
import config from 'config';
import {expect} from 'chai';
import Nightmare from 'nightmare';
import url from 'url';
import os from 'os';

let osType = os.type();
describe('authorization steps', function () {
  let connector;
  let clientId = config.get('clientId');
  let clientSecret = config.get('clientSecret');
  let nightmare;

  before(function () {
    if (osType !== 'Linux') {
      nightmare = Nightmare({show: true});
    }
    connector = new WordpressConnector({clientId, clientSecret});

  });
  after(function () {
    if (osType !== 'Linux') {
      return nightmare.end();
    }
  })
  describe('on first bounce', () => {
    let bounce;
    before(function () {

      bounce = {
        get: function () {
          return undefined;
        },
        delete: function () {
          return Promise.resolve(null);
        },
        set: function () {
          console.log('set', arguments);
          return Promise.resolve(null);
        },
        redirect: function () {
          console.log('redirect', arguments);
          return Promise.resolve(null);
        },
        done: function () {
          console.log('done', arguments);
          return Promise.resolve(null);
        }
      };
      sinon.spy(bounce, 'get');
      sinon.spy(bounce, 'set');
      sinon.spy(bounce, 'redirect');
      return connector.receiveBounce(bounce);
    });
    it('should save current step', () => {
      return expect(bounce.set).to.have.been.calledWith('currentStep', 'authorization');
    });
    it('should receive a redirect', () => {
      return expect(bounce.redirect).to.have.been.calledWith(`https://public-api.wordpress.com/oauth2/authorize?redirect_uri=https%3A%2F%2Fbouncer.hoist.test%2Fbounce&response_type=code&scope=global&client_id=${clientId}`);
    });
  });
  describe('on return from Wordpress', function () {
    let bounce;
    this.timeout(50000);
    let uri;

    before(function () {
      if (osType === 'Linux') {
        this.skip();
      }
      bounce = {
        store: {},
        get: function (key) {
          return this.store[key];
        },
        delete: function (key) {
          delete this.store[key];
          return Promise.resolve(null);
        },
        set: function (key, value) {
          this.store[key] = value;
          return Promise.resolve(null);
        },
        redirect: function () {
          console.log('redirect', arguments);
          return Promise.resolve(null);
        },
        done: function () {
          console.log('done', arguments);
          return Promise.resolve(null);
        }
      };
      sinon.spy(bounce, 'get');
      sinon.spy(bounce, 'set');
      sinon.spy(bounce, 'redirect');
      return Promise.resolve(connector.receiveBounce(bounce)).then(function () {
        let authorizeUri = bounce.redirect.getCall(0).args[0];
        return Promise.resolve(nightmare.goto(authorizeUri));
      }).then(() => {
        return Promise.resolve(nightmare.type('#username', config.get('username')).type('#password', config.get('password')).click('.signin.button-primary'));
      }).then(() => {
        return Promise.resolve(nightmare.wait(500).exists('#message'));
      }).then((finished) => {
        if (finished) {
          Promise.resolve(nightmare.url())
        } else {
          return Promise.resolve(nightmare.click('.button-primary.approve').wait('#message').url());
        }
      }).then((u) => {
        console.log(u);
        uri = url.parse(u, true);
        bounce.query = uri.query;
      }).then(() => {
        return connector.receiveBounce(bounce);
      });

    });
    it('should save state', () => {
      console.log(bounce.store);
      return expect(bounce.store['AccessToken']).to.exist;
    });
  })
});

